import { Component } from '@angular/core';
import { ViewChild } from '@angular/core';
import { ElementRef } from '@angular/core';
import { User } from '../app.component';

@Component({
  selector: 'app-registering',
  templateUrl: './registering.component.html',
  styleUrls: ['./registering.component.css']
})
export class RegisteringComponent {
  @ViewChild('name') name:ElementRef;
  @ViewChild('mail') mail:ElementRef;
  @ViewChild('pass') pass:ElementRef;

  callbackRequest:Function;

  register(){
    const us: User=({
      nick: this.name.nativeElement.value,
      email: this.mail.nativeElement.value,
      password: this.pass.nativeElement.value
    });

    let fun = this.callbackRequest(us);
    fun.subscribe((res:User)=>{
        console.log(res);
    })

  }
}
<div class = "container">
    <div class = "form"#form>
            <div class = "inputs" >
                <p>Nick:</p>
                <input #name class = "nick" type="text">
            </div>
            <div class = "inputs">
                <p>EMail:</p>
                <input #mail class = "email" type="tel">
            </div>
            <div class = "inputs">
                <p>Password:</p>
                <input #pass class = "password" type="tel">
            </div>
            <div class = "inputs">
                <button (click)="register();" class = "add">Dodaj</button>
            </div>
    </div>
</div>
