import { Component } from '@angular/core';
// import dishes_data from 'dishes_data.json';

@Component({
  selector: 'app-menu',
  templateUrl: './menu.component.html',
  styleUrls: ['./menu.component.css']
})
export class MenuComponent {

}
// const btn = document.querySelector('.add');

class Dish {
  name: string;
  type: string;
  category: string;
  ingredients: string[];
  max_number_per_day: number;
  price: number;
  description: string;
  link: string;

  constructor(name: string, type: string, category: string, ingredients: string[], max_number_per_day: number, price: number, description: string, link: string){
    this.name = name;
    this.type = type;
    this.category = category;
    this.ingredients = ingredients;
    this.max_number_per_day = max_number_per_day
    this.price = price;
    this.description = description;
    this.link = link;
  }
}

function create(object:Dish){
  let name = object.name;
  let type = object.type;
  let category = object.category;
  let ingredients = object.ingredients;
  let max_number_per_day = object.max_number_per_day;
  let price = object.price;
  let description = object.description;
  let link = object.link;

  const el = document.createElement("div");

  el.classList.add("book")
  el.style.setProperty("background-color", "#B0B0B0");

  let bin = document.createElement("button");
  bin.classList.add("material-symbols-outlined")
  bin.classList.add("bin")
  bin.innerText = "delete";
  // bin.addEventListener('click', delete_div, false)
  // bins_btn.push(bin)

  const name_text = document.createElement("div");
  name_text.innerText = name
  name_text.classList.add("data1")

  const type_text = document.createElement("div");
  type_text.innerText = type
  type_text.classList.add("data1")

  const category_text = document.createElement("div");
  category_text.innerText = type
  category_text.classList.add("data1")

  const ingredients_text = document.createElement("div");
  let str = "";
  for(let i = 0; i < ingredients.length; i++){
    str = str + ingredients[i];
  }
  ingredients_text.innerText = str
  ingredients_text.classList.add("data1")

  const max_number_text = document.createElement("div");
  max_number_text.innerText = max_number_per_day.toString();
  max_number_text.classList.add("data1")

  const price_text = document.createElement("div");
  price_text.innerText = price.toString();
  price_text.classList.add("data1")

  const description_text = document.createElement("div");
  description_text.innerText = description;
  description_text.classList.add("data1")

  const link_text = document.createElement("div");
  link_text.innerText = link;
  link_text.classList.add("data1")

  // let books_ = document.getElementsByClassName('books');
  // const books = document.getElementById('books');
  // const arr = Array.from(books_);
  // let books = arr[0];
  // if(books != null){

  @ViewChild('books') div: ElementRef;
  books?.appendChild(el);
  el.appendChild(name_text)
  el.appendChild(type_text)
  el.appendChild(category_text)
  el.appendChild(ingredients_text)
  el.appendChild(max_number_text)
  el.appendChild(price_text)
  el.appendChild(description_text)
  el.appendChild(link_text)
  el.appendChild(bin)
  //}
}


let object = new Dish("Kotlet", "Danie obiadowe", "Danie mięsne", ["mięso schabowe", "ziemniaki"], 12, 13.20, "Pyszny domowy kotlecik!", "link");
var objects:Dish[];
objects = [object];

for(let i = 0; i<objects.length; i++){
  let obj = objects[i];
  create(obj);
}










