import { Component, OnInit, Input } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Dish } from '../app.component';
import { Observable } from 'rxjs';

@Component({
  selector: 'app-one-dish',
  templateUrl: './one-dish.component.html',
  styleUrls: ['./one-dish.component.css']
})
export class OneDishComponent {
  public id: string;
  constructor(private route: ActivatedRoute) {}
  dish:Dish

  // @Input() callbackRequest:any;;
  public callbackRequest: any;;

  ngOnInit(){
    let i = this.route.snapshot.paramMap.get('id');
    console.log(i)
    // this.callbackRequest.subscribe((res:Dish)=>{
    //   console.log(res);
    // })
    let fun = this.callbackRequest(1)
    fun.subscribe((res:Dish)=>{
        console.log(res);
        this.dish = res;
    })



  }

}
<div class = "container">
    <div id="books"#books>

        <div class = "name text">{{dish.name}}</div>

        <div class = "slaider">
                <div class="left_arrow"></div>
                <img class = "images" src={{dish.link}} />
                <div class="right_arrow"></div>
        </div>

        <div class = 'data'>
            <div class = "type text">
                <div class='text'>Rodzaj:</div>
                {{dish.type}}
            </div>
            <div class = "category text"><div class='text'>Kategoria:</div>{{dish.category}}</div>
            <div class = "ingredients text"><div class='text'>Składniki:</div>{{dish.ingredients}}</div>
            <div class = "max_number text"><div class='text'>Maksymalna liczba na dzień:</div>{{dish.max_number_per_day}}</div>
            <div class = "price text"><div class='text'>Cena:</div>{{dish.price}}</div>
            <div class = "desription text"><div class='text'>Opis:</div>{{dish.description}}</div>
        </div>




            <!-- <button (onclick)="add_to_cart(obj.id)" class = "cart">
                <span class="material-symbols-outlined">
                  add_shopping_cart
                </span>
            </button> -->
    </div>
</div>
